import React from "react";

export const ProductDetails = () => {
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-lg w-full max-w-md p-6 text-center">
        {/* Imagem do Produto */}
        <div className="w-full h-60 bg-gray-200 rounded-xl flex items-center justify-center mb-6">
          <span className="text-gray-500">Imagem do Produto</span>
        </div>

        {/* Informações do Produto */}
        <h2 className="text-2xl font-semibold text-gray-800">
          Nome do Produto
        </h2>
        <p className="text-lg text-gray-600 mt-2">R$ 99,90</p>

        {/* Botão Adicionar ao Carrinho */}
        <button className="w-full mt-6 py-3 bg-gradient-to-r from-blue-500 to-blue-700 text-white rounded-xl font-medium hover:from-blue-600 hover:to-blue-800 transition duration-300 shadow-md">
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  );
};

