import React from "react";

export const ProductDetails = () => {
  return (
    <div className="bg-gray-100 flex flex-col items-center justify-center min-h-screen p-4">
      {/* Container do Produto */}
      <div className="w-full max-w-2xl text-center">
        {/* Imagem do Produto */}
        <div className="w-full h-72 bg-gray-300 rounded-lg flex items-center justify-center shadow-md">
          <span className="text-gray-500 text-lg">Imagem do Produto</span>
        </div>

        {/* Informações do Produto */}
        <div className="mt-6">
          <h2 className="text-2xl font-bold text-gray-800">Nome do Produto</h2>
          <p className="text-xl text-gray-600 mt-2">R$ 99,90</p>
        </div>

        {/* Botão Adicionar ao Carrinho */}
        <button className="w-full max-w-xs bg-blue-500 text-white py-3 mt-6 rounded-lg hover:bg-blue-600 transition duration-300 shadow-md">
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  );
};

